var styles = ".h-250 {\n  height: 250px;\n}\n.h-200 {\n  height: 200px;\n}\n.lb-grid {\n  position: relative;\n  display: block;\n}\n\n.lb-item {\n  position: absolute;\n  background-position: center center;\n  background-repeat: no-repeat;\n  background-size: cover;\n  border-top: solid 2px #fff;\n  border-right: solid 2px #fff;\n}\n\n.lb-item:hover,\n.lb-item:focus {\n  opacity: 0.6;\n}\n\n\n.lb-grid-1 .lb-item {\n  width: 100%;\n  height: 100%;\n}\n\n.lb-grid-2 .lb-item,\n.lb-grid-3 .lb-item,\n.lb-grid-4 .lb-item,\n.lb-grid-5 .lb-item {\n  width: 50%;\n}\n\n.lb-grid-2 .lb-item,\n.lb-grid-3 .lb-item:nth-child(1),\n.lb-grid-4 .lb-item:nth-child(1) {\n  height: 100%;\n}\n\n.lb-grid-3 .lb-item:nth-child(2),\n.lb-grid-3 .lb-item:nth-child(3),\n.lb-grid-5 .lb-item:nth-child(1),\n.lb-grid-5 .lb-item:nth-child(2) {\n  height: 50%;\n}\n\n.lb-item:last-child,\n.lb-grid-2 .lb-item:nth-child(2),\n.lb-grid-3 .lb-item:nth-child(2),\n.lb-grid-3 .lb-item:nth-child(3),\n.lb-grid-4 .lb-item:nth-child(2),\n.lb-grid-4 .lb-item:nth-child(3),\n.lb-grid-4 .lb-item:nth-child(4),\n.lb-grid-5 .lb-item:nth-child(3),\n.lb-grid-5 .lb-item:nth-child(4),\n.lb-grid-5 .lb-item:nth-child(5) {\n  left: auto;\n  right: 0;\n  border-right: 0;\n}\n\n.lb-grid-3 .lb-item:nth-child(3),\n.lb-grid-4 .lb-item:nth-child(4),\n.lb-grid-5 .lb-item:nth-child(2),\n.lb-grid-5 .lb-item:nth-child(5) {\n  bottom: 0;\n  top: auto;\n}\n\n.lb-grid-4 .lb-item:nth-child(3),\n.lb-grid-5 .lb-item:nth-child(4) {\n  top: 33.333333333333336%;\n}\n\n.lb-grid-4 .lb-item:nth-child(2),\n.lb-grid-4 .lb-item:nth-child(3),\n.lb-grid-4 .lb-item:nth-child(4),\n.lb-grid-5 .lb-item:nth-child(3),\n.lb-grid-5 .lb-item:nth-child(4),\n.lb-grid-5 .lb-item:nth-child(5) {\n  height: 33.333333333333336%;\n}\n\n.lb-more {\n  position: absolute;\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  text-align: center;\n  color: #fff;\n  font-size: 3rem;\n  background-color: rgba(0, 0, 0, .4);\n}\n\n.lb-more:before {\n  display: inline-block;\n  content: \"\";\n  vertical-align: middle;\n  height: 100%;\n}\n\n.lb-modal {\n  transition: opacity .2s ease;\n  position: fixed;\n  z-index: 99999;\n  top: 0;\n  left: 0;\n  bottom: 0;\n  right: 0;\n  width: 100%;\n  min-height: 100%;\n  height: 100vh;\n  background-color: rgba(0, 0, 0, .8);\n  display: block;\n  -webkit-user-select: none;\n  -moz-user-select: -moz-none;\n  -khtml-user-select: none;\n  -o-user-select: none;\n  user-select: none;\n}\n\n.lb-modal-close {\n  position: absolute !important;\n  right: 20px;\n  top: 20px;\n  color: #fff;\n}\n\n.lb-modal-next,\n.lb-modal-prev {\n  position: absolute !important;\n  top: 50%;\n  left: 20px;\n  margin-top: -25px;\n  /* width: 50px;\n    height: 50px; */\n  z-index: 999;\n  cursor: pointer;\n  color: #fff;\n  background-color: transparent;\n  border: none;\n}\n\n.lb-modal-next {\n  left: auto;\n  right: 20px;\n}\n\n.lb-modal-img {\n  position: absolute;\n  top: 10px;\n  left: 70px;\n  right: 70px;\n  bottom: 10px;\n  text-align: center;\n}\n\n.lb-modal-img:before {\n  display: inline-block;\n  content: \"\";\n  vertical-align: middle;\n  height: 100%;\n}\n\n.lb-modal-img img {\n  max-width: 100%;\n  max-height: 100%;\n  vertical-align: middle;\n  object-fit: contain;\n}\n\n.lb-modal-img .spinner {\n  width: 150px;\n  position: absolute;\n  margin-left: -75px;\n  top: 50%;\n  left: 50%;\n  z-index: 1;\n}\n";export{styles as default};